import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import APIInvoke from '../../utils/APIInvoke'
import swal from 'sweetalert'



const Login = () => {


    /// metodo pata direcciona de un compnente a otro 

    const navigate = useNavigate();

    const [usuario, setusuario] = useState ({

        email: '',
        contrasena: ''

    });

    const { email, contrasena } = usuario;

    const onChange = (e) => {

         setusuario ({
            ...usuario,
           [e.target.name] : e.target.value
         })
    }

    useEffect ( () => {
        document.getElementById("email").focus();

    }, [])

    const iniciarSesion = async () => {

        if (contrasena.length < 6) {
            const msg = "La contraseÃ±a debe ser al menos de 6 caracteres";
            swal ({
               title: 'Error',
               text: msg,
               icon: 'error',
               buttons: {
                confirm : { 
                    text: 'Ok',
                    value: true,
                    visible: true,
                    className: 'btn btn-danger',
                    closeModal: true

                }
               }
            });
         } else {

            const data = {
                email: usuario.email,
                contrasena: usuario.contrasena,
             }          
    
            const response = await APIInvoke.invokePOST('/api/auth', data);
            const mensaje = response.msg;
            if (mensaje === 'El usuario no existe' || mensaje === 'El usuario no existe' ) {


            }
         }
    }

    
    const onSubmit = (e) => {
        e.preventDefault();
        iniciarSesion();

    }

    return (
        <div class='hold-transition login-page'>

            <div className="login-box">
                <div className="login-logo">
                    <Link to="#"><b>MiDulceOnline</b></Link>
                </div>
                {/* /.login-logo */}
                <div className="card">

                    <div className="card-body login-card-body">
                        <p className="login-box-msg">Iniciar Sesion</p>
                        
                        <form onSubmit={onSubmit}>

                            <div className="input-group mb-3">
                                <input type="email"
                                    className="form-control"
                                    placeholder="Email"
                                    id="email"
                                    name="email"
                                    value={email}
                                    onChange={onChange}
                                    required
                                 />

                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-envelope" />
                                    </div>
                                </div>
                            </div>

                            <div className="input-group mb-3">
                                <input type="password"
                                    className="form-control"
                                    placeholder="ContraseÃ±a"
                                    id="contrasena"
                                    name="contrasena"
                                    value={contrasena}
                                    onChange={onChange}
                                    required
                                 />
                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-lock" />
                                    </div>
                                </div>

                            </div>
                            <div className="row">

                                <div className="col-6">
                                 <button type="submit" className="btn btn-primaryblock btn-primary">Iniciar </button>
                                </div>

                                <div className="col-6">
                                    <Link to="/crear-login" class="btn btn-block btn-primary">Nuevo Usuario</Link>
                                </div>

                                {/* /.col */}
                            </div>

                        </form>



                    </div>
                </div>
            </div>
        </div >
    )
};

export default Login;
